const DayCell = styled.div<{ 
  isCurrentMonth?: boolean; 
  isToday?: boolean; 
  hasEvent?: boolean;
  isWeekend?: boolean;
}>`
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.875rem;
  font-weight: ${props => props.isToday ? '700' : '500'};
  border: 1px solid #e2e8f0;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  position: relative;

  background: ${props => {
    if (props.hasEvent) return '#fef3c7'; // Amarillo para eventos
    if (props.isToday) return '#3b82f6';
    if (props.isWeekend) return '#f8fafc';
    return 'white';
  }};

  color: ${props => {
    if (props.isToday) return 'white';
    if (props.hasEvent) return '#92400e';
    if (!props.isCurrentMonth) return '#cbd5e1';
    return '#374151';
  }};

  &:hover {
    background: ${props => props.hasEvent ? '#fde68a' : '#f1f5f9'};
    transform: scale(1.05);
    z-index: 1;
  }

  ${props => !props.isCurrentMonth && `
    background: #f8fafc;
    color: #cbd5e1;
  `}
`;

const Events: React.FC = () => {
  const [selectedSalon, setSelectedSalon] = useState('San Telmo');
  const [selectedZone, setSelectedZone] = useState('TODAS');
  const [selectedYear, setSelectedYear] = useState('2025');

  // Datos de ejemplo para eventos
  const eventDates = [
    // Enero 2025
    '2025-01-04', '2025-01-05', '2025-01-11', '2025-01-12', '2025-01-18', '2025-01-19', '2025-01-25', '2025-01-26',
    // Febrero 2025
    '2025-02-01', '2025-02-02', '2025-02-08', '2025-02-09', '2025-02-15', '2025-02-16', '2025-02-22', '2025-02-23',
    // Marzo 2025
    '2025-03-01', '2025-03-02', '2025-03-08', '2025-03-09', '2025-03-15', '2025-03-16', '2025-03-22', '2025-03-23', '2025-03-29', '2025-03-30',
    // Abril 2025
    '2025-04-04', '2025-04-05', '2025-04-06', '2025-04-11', '2025-04-12', '2025-04-13', '2025-04-17', '2025-04-18', '2025-04-19', '2025-04-20', '2025-04-24', '2025-04-25', '2025-04-26', '2025-04-27',
    // Mayo 2025
    '2025-05-02', '2025-05-03', '2025-05-04', '2025-05-09', '2025-05-10', '2025-05-11',
    // Junio 2025
    '2025-06-06', '2025-06-07', '2025-06-08'
  ];

  const months = [
    { name: 'ENERO', year: 2025, month: 0 },
    { name: 'FEBRERO', year: 2025, month: 1 },
    { name: 'MARZO', year: 2025, month: 2 },
    { name: 'ABRIL', year: 2025, month: 3 },
    { name: 'MAYO', year: 2025, month: 4 },
    { name: 'JUNIO', year: 2025, month: 5 }
  ];

  const dayNames = ['Lun', 'Mar', 'Mié', 'Jue', 'Vie', 'Sab', 'Dom'];

  const renderMonthCalendar = (monthData: { name: string; year: number; month: number }) => {
    const { year, month } = monthData;
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startDay = firstDay.getDay();

    // Ajustar para que la semana empiece en lunes (0 = lunes, 6 = domingo)
    const adjustedStartDay = startDay === 0 ? 6 : startDay - 1;

    const today = new Date();
    const isCurrentMonth = today.getFullYear() === year && today.getMonth() === month;

    const days = [];

    // Días vacíos al inicio
    for (let i = 0; i < adjustedStartDay; i++) {
      days.push(<DayCell key={`empty-${i}`} isCurrentMonth={false} />);
    }

    // Días del mes
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const dateString = date.toISOString().split('T')[0];
      const isToday = today.getDate() === day && today.getFullYear() === year && today.getMonth() === month;
      const hasEvent = eventDates.includes(dateString);
      const isWeekend = date.getDay() === 0 || date.getDay() === 6;

      days.push(
        <DayCell
          key={day}
          isCurrentMonth={true}
          isToday={isToday}
          hasEvent={hasEvent}
          isWeekend={isWeekend}
          title={hasEvent ? `Evento programado para el ${day}/${month + 1}/${year}` : ''}
        >
          {day}
        </DayCell>
      );
    }

    return days;
  };

  return (
    <Layout 
      title="Calendario de Eventos" 
      description="Vista anual del calendario de eventos del salón"
    >
      <EventsContainer>
        <HeaderSection>
          <HeaderContent>
            <HeaderLeft>
              <FaBars style={{ fontSize: '1.25rem' }} />
              <h1>Janos's Tecnica</h1>
            </HeaderLeft>
            <HeaderRight>
              <div className="user-info">
                <FaUsers />
                <span>sebastian_maza</span>
              </div>
            </HeaderRight>
          </HeaderContent>
        </HeaderSection>

        <FilterSection>
          <FilterGrid>
            <FilterGroup>
              <label>Salon:</label>
              <select value={selectedSalon} onChange={(e) => setSelectedSalon(e.target.value)}>
                <option value="San Telmo">San Telmo</option>
                <option value="Palermo">Palermo</option>
                <option value="Recoleta">Recoleta</option>
                <option value="Belgrano">Belgrano</option>
              </select>
            </FilterGroup>

            <FilterGroup>
              <label>Zona:</label>
              <select value={selectedZone} onChange={(e) => setSelectedZone(e.target.value)}>
                <option value="TODAS">TODAS</option>
                <option value="NORTE">NORTE</option>
                <option value="SUR">SUR</option>
                <option value="ESTE">ESTE</option>
                <option value="OESTE">OESTE</option>
              </select>
            </FilterGroup>

            <FilterGroup>
              <label>Año:</label>
              <select value={selectedYear} onChange={(e) => setSelectedYear(e.target.value)}>
                <option value="2024">2024</option>
                <option value="2025">2025</option>
                <option value="2026">2026</option>
              </select>
            </FilterGroup>
          </FilterGrid>

          <ButtonGroup>
            <ActionButton variant="primary">
              <FaFilter />
              Filtrar
            </ActionButton>
            <ActionButton variant="secondary">
              <FaDownload />
              Exportar
            </ActionButton>
          </ButtonGroup>
        </FilterSection>

        <CurrentView>
          <div>
            <div className="view-title">{selectedSalon} - {selectedYear}</div>
            <div className="section-title">Eventos</div>
          </div>
        </CurrentView>

        <CalendarGrid>
          {months.map((month) => (
            <MonthCalendar key={month.month}>
              <MonthHeader>
                <h3>{month.name}</h3>
              </MonthHeader>

              <DaysHeader>
                {dayNames.map((day) => (
                  <DayHeader key={day}>{day}</DayHeader>
                ))}
              </DaysHeader>

              <DaysGrid>
                {renderMonthCalendar(month)}
              </DaysGrid>
            </MonthCalendar>
          ))}
        </CalendarGrid>
      </EventsContainer>
    </Layout>
  );
};

export default Events;
