import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './context/AuthContext';
import { CoordinationProvider } from './context/CoordinationContext';
import { TimeClockProvider } from './context/TimeClockContext';
import { GlobalStyles } from './styles/GlobalStyles';
import Layout from './components/Layout';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Events from './pages/Events';
import TimeClock from './pages/TimeClock';
import TechCheck from './pages/TechCheck';
import Orders from './pages/Orders';
import Shows from './pages/Shows';
import Visuals from './pages/Visuals';
import Music from './pages/Music';
import Software from './pages/Software';
import Returns from './pages/Returns';
import Coordination from './pages/Coordination';
import TechSupport from './components/TechSupport';

// Componente para proteger rutas
const ProtectedRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        fontSize: '1.125rem',
        color: '#64748b'
      }}>
        Cargando...
      </div>
    );
  }

  if (!user) {
    return <Navigate to="/login" replace />;
  }

  return <>{children}</>;
};

// Componente para redirigir usuarios autenticados
const PublicRoute: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const { user, isLoading } = useAuth();

  if (isLoading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        fontSize: '1.125rem',
        color: '#64748b'
      }}>
        Cargando...
      </div>
    );
  }

  if (user) {
    return <Navigate to="/dashboard" replace />;
  }

  return <>{children}</>;
};

const AppRoutes: React.FC = () => {
  return (
    <Routes>
      <Route 
        path="/login" 
        element={
          <PublicRoute>
            <Login />
          </PublicRoute>
        } 
      />
      <Route 
        path="/dashboard" 
        element={
          <ProtectedRoute>
            <Layout title="Dashboard" description="Panel principal de administración">
              <Dashboard />
            </Layout>
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/events" 
        element={
          <ProtectedRoute>
            <Layout title="Eventos" description="Gestión de eventos y calendario anual">
              <Events />
            </Layout>
          </ProtectedRoute>
        } 
      />
      <Route 
        path="/timeclock" 
        element={
          <ProtectedRoute>
            <Layout title="Sistema de Fichajes" description="Control de entrada y salida de empleados">
              <TimeClock />
            </Layout>
          </ProtectedRoute>
        } 
      />
      <Route
        path="/techcheck"
        element={
          <ProtectedRoute>
            <Layout title="Check In Técnico" description="Verificación de equipamiento técnico">
              <TechCheck />
            </Layout>
          </ProtectedRoute>
        }
      />
        <Route
          path="/orders"
          element={
            <ProtectedRoute>
              <Layout title="Pedidos" description="Solicitudes al departamento técnico">
                <Orders />
              </Layout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/shows"
          element={
            <ProtectedRoute>
              <Layout title="Shows" description="Pistas de audio para shows artísticos">
                <Shows />
              </Layout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/visuals"
          element={
            <ProtectedRoute>
              <Layout title="Visuales" description="Material visual para salones con pantalla">
                <Visuals />
              </Layout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/music"
          element={
            <ProtectedRoute>
              <Layout title="Música" description="Biblioteca musical colaborativa">
                <Music />
              </Layout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/software"
          element={
            <ProtectedRoute>
              <Layout title="Software" description="Herramientas y software para DJs">
                <Software />
              </Layout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/returns"
          element={
            <ProtectedRoute>
              <Layout title="Mis Devoluciones" description="Gestión de devoluciones y nivel de perfil">
                <Returns />
              </Layout>
            </ProtectedRoute>
          }
        />
        <Route
          path="/coordination"
          element={
            <ProtectedRoute>
              <Layout title="Coordinación con DJ" description="Sistema de preguntas para coordinación previa">
                <Coordination />
              </Layout>
            </ProtectedRoute>
          }
        />
      <Route 
        path="/" 
        element={<Navigate to="/dashboard" replace />} 
      />
      <Route 
        path="*" 
        element={<Navigate to="/dashboard" replace />} 
      />
    </Routes>
  );
};

const App: React.FC = () => {
  return (
    <AuthProvider>
      <CoordinationProvider>
        <TimeClockProvider>
          <GlobalStyles />
          <Router>
            <AppRoutes />
            <TechSupport />
          </Router>
        </TimeClockProvider>
      </CoordinationProvider>
    </AuthProvider>
  );
};

export default App;
