    },
    {
      id: '4',
      name: 'Traktor Pro',
      category: 'Software de DJ',
      version: '3.8.0',
      fileSize: '41.3 MB',
      platform: ['Windows', 'Mac'],
      description: 'Software de DJ de Native Instruments con efectos únicos, loops y remix decks avanzados.',
