import React, { useState } from 'react';
import styled from 'styled-components';
import Layout from '../components/Layout';
import { 
  FaCalendarAlt, 
  FaPlus, 
  FaSearch, 
  FaFilter,
  FaEye,
  FaEdit,
  FaTrash,
  FaCheckCircle,
  FaClock,
  FaExclamationTriangle,
  FaUsers,
  FaMapMarkerAlt,
  FaMusic,
  FaChevronLeft,
  FaChevronRight,
  FaDownload,
  FaBars,
  FaWhatsapp
} from 'react-icons/fa';
import { Event } from '../types';

const EventsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const HeaderSection = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 1.5rem;
  border-radius: 1rem;
  margin-bottom: 1rem;
`;

const HeaderContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const HeaderLeft = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  h1 {
    font-size: 1.5rem;
    font-weight: 700;
    margin: 0;
  }
`;

const HeaderRight = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  .user-info {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.875rem;
    opacity: 0.9;
  }
`;

const FilterSection = styled.div`
  background: white;
  padding: 1.5rem;
  border-radius: 1rem;
  box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.1);
  margin-bottom: 1.5rem;
`;

const FilterGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  align-items: end;
  margin-bottom: 1rem;
`;

const FilterGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;

  label {
    font-size: 0.875rem;
    font-weight: 600;
    color: #374151;
  }

  select {
    padding: 0.75rem;
    border: 1px solid #e2e8f0;
    border-radius: 0.5rem;
    font-size: 0.875rem;
    background: white;

    &:focus {
      outline: none;
      border-color: #3b82f6;
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const ActionButton = styled.button<{ variant?: 'primary' | 'secondary' }>`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 0.5rem;
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  background: ${props => props.variant === 'primary' ? '#3b82f6' : '#f3f4f6'};
  color: ${props => props.variant === 'primary' ? 'white' : '#374151'};

  &:hover {
    background: ${props => props.variant === 'primary' ? '#2563eb' : '#e5e7eb'};
    transform: translateY(-1px);
  }
`;

const CurrentView = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;

  .view-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: #1e293b;
  }

  .section-title {
    font-size: 1.125rem;
    font-weight: 600;
    color: #64748b;
  }
`;

const CalendarGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1.5rem;
  margin-top: 1rem;
`;

const MonthCalendar = styled.div`
  background: white;
  border-radius: 1rem;
  padding: 1.5rem;
  box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.1);
  border: 1px solid #e5e7eb;
`;

const MonthHeader = styled.div`
  text-align: center;
  margin-bottom: 1rem;

  h3 {
    font-size: 1.25rem;
    font-weight: 700;
    color: #1e293b;
    margin: 0;
    text-transform: uppercase;
  }
`;

const DaysHeader = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
  margin-bottom: 0.5rem;
`;

const DayHeader = styled.div`
  text-align: center;
  font-size: 0.75rem;
  font-weight: 600;
  color: #64748b;
  padding: 0.5rem 0;
`;

const DaysGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
`;

const DayCell = styled.div<{ 
  isCurrentMonth?: boolean; 
  isToday?: boolean; 
  hasEvent?: boolean;
  isWeekend?: boolean;
  isCoordinated?: boolean;
}>`
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.875rem;
  font-weight: ${props => props.isToday ? '700' : '500'};
  border: 1px solid #e2e8f0;
  cursor: ${props => props.hasEvent ? 'pointer' : 'default'};
  transition: all 0.2s ease-in-out;
  position: relative;

  background: ${props => {
    if (props.hasEvent && props.isCoordinated) return '#dcfce7'; // Verde para eventos coordinados
    if (props.hasEvent && !props.isCoordinated) return '#fee2e2'; // Rojo para eventos no coordinados
    if (props.isToday) return '#3b82f6';
    if (props.isWeekend) return '#f8fafc';
    return 'white';
  }};

  color: ${props => {
    if (props.isToday) return 'white';
    if (props.hasEvent && props.isCoordinated) return '#166534'; // Verde oscuro para texto
    if (props.hasEvent && !props.isCoordinated) return '#991b1b'; // Rojo oscuro para texto
    if (!props.isCurrentMonth) return '#cbd5e1';
    return '#374151';
  }};

  &:hover {
    background: ${props => {
      if (props.hasEvent && props.isCoordinated) return '#bbf7d0'; // Verde más claro al hover
      if (props.hasEvent && !props.isCoordinated) return '#fecaca'; // Rojo más claro al hover
      return '#f1f5f9';
    }};
    transform: scale(1.05);
    z-index: 1;
  }

  ${props => !props.isCurrentMonth && `
    background: #f8fafc;
    color: #cbd5e1;
  `}
`;

