import React from 'react';
import styled from 'styled-components';
import { NavLink, useLocation } from 'react-router-dom';
import { 
  FaHome, 
  FaCalendarAlt, 
  FaMusic, 
  FaTools, 
  FaUsers, 
  FaChartBar,
  FaCog,
  FaSignOutAlt,
  FaUser,
  FaClock,
  FaMapMarkerAlt,
  FaClipboardCheck,
  FaBars,
  FaChevronLeft,
  FaChevronRight
} from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';

const SidebarContainer = styled.div<{ isCollapsed: boolean }>`
  width: ${props => props.isCollapsed ? '80px' : '280px'};
  height: 100vh;
  background: linear-gradient(180deg, #1e293b 0%, #334155 100%);
  color: white;
  position: fixed;
  left: 0;
  top: 0;
  overflow-y: auto;
  z-index: 1000;
  box-shadow: 2px 0 10px rgba(0, 0, 0, 0.1);
  transition: width 0.3s ease-in-out;
`;

const SidebarHeader = styled.div<{ isCollapsed: boolean }>`
  padding: ${props => props.isCollapsed ? '1rem 0.5rem' : '2rem 1.5rem 1.5rem'};
  border-bottom: 1px solid #475569;
  position: relative;
  
  .logo {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    margin-bottom: 1rem;
    
    .logo-icon {
      font-size: 1.5rem;
      color: #3b82f6;
      min-width: 24px;
    }
    
    h2 {
      font-size: 1.25rem;
      font-weight: 700;
      color: white;
      opacity: ${props => props.isCollapsed ? '0' : '1'};
      transition: opacity 0.3s ease;
      white-space: nowrap;
    }
  }
  
  .user-info {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    padding: 0.75rem;
    background: rgba(59, 130, 246, 0.1);
    border-radius: 0.5rem;
    border: 1px solid rgba(59, 130, 246, 0.2);
    
    .user-avatar {
      width: 32px;
      height: 32px;
      border-radius: 50%;
      background: #3b82f6;
      display: flex;
      align-items: center;
      justify-content: center;
      font-size: 0.875rem;
      font-weight: 600;
      min-width: 32px;
    }
    
    .user-details {
      flex: 1;
      opacity: ${props => props.isCollapsed ? '0' : '1'};
      transition: opacity 0.3s ease;
      overflow: hidden;
      
      .user-name {
        font-weight: 600;
        font-size: 0.875rem;
        color: white;
        margin-bottom: 0.25rem;
        white-space: nowrap;
      }
      
      .user-role {
        font-size: 0.75rem;
        color: #94a3b8;
        text-transform: capitalize;
        white-space: nowrap;
      }
    }
  }
  
  .toggle-button {
    position: absolute;
    top: 1rem;
    right: ${props => props.isCollapsed ? '0.5rem' : '1.5rem'};
    background: rgba(59, 130, 246, 0.2);
    border: none;
    color: white;
    width: 32px;
    height: 32px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.2s;
    
    &:hover {
      background: rgba(59, 130, 246, 0.3);
      transform: scale(1.1);
    }
  }
`;

const NavMenu = styled.nav`
  padding: 1.5rem 0;
`;

