import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { 
  FaCalendarAlt, 
  FaPlus, 
  FaSearch, 
  FaFilter,
  FaEye,
  FaEdit,
  FaTrash,
  FaCheckCircle,
  FaClock,
  FaExclamationTriangle,
  FaUsers,
  FaMapMarkerAlt,
  FaMusic,
  FaChevronLeft,
  FaChevronRight,
  FaDownload,
  FaBars,
  FaWhatsapp,
  FaArrowLeft
} from 'react-icons/fa';
import { Event } from '../types';

const EventsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const HeaderSection = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
  padding: 2rem;
  border-radius: 1.5rem;
  margin-bottom: 1.5rem;
  position: relative;
  overflow: hidden;
  box-shadow: 0 20px 40px rgba(102, 126, 234, 0.3);

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    right: -50%;
    width: 200%;
    height: 200%;
    background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
    animation: float 8s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-30px) rotate(180deg); }
  }
`;

const HeaderContent = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const HeaderLeft = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  h1 {
    font-size: 1.5rem;
    font-weight: 700;
    margin: 0;
  }
`;

const HeaderRight = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;

  .user-info {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-size: 0.875rem;
    opacity: 0.9;
  }
`;

const BackButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  background: rgba(255, 255, 255, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.3);
  color: white;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 0.2s;
  font-size: 0.875rem;
  font-weight: 500;

  &:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: translateY(-1px);
  }
`;

const FilterSection = styled.div`
  background: white;
  padding: 1.5rem;
  border-radius: 1rem;
  box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.1);
  margin-bottom: 1.5rem;
`;

const FilterGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  align-items: end;
  margin-bottom: 1rem;
`;

const FilterGroup = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;

  label {
    font-size: 0.875rem;
    font-weight: 600;
    color: #374151;
  }

  select {
    padding: 0.75rem;
    border: 1px solid #e2e8f0;
    border-radius: 0.5rem;
    font-size: 0.875rem;
    background: white;

    &:focus {
      outline: none;
      border-color: #3b82f6;
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
`;

const ActionButton = styled.button<{ variant?: 'primary' | 'secondary' }>`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  border: none;
  border-radius: 0.5rem;
  font-weight: 600;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  background: ${props => props.variant === 'primary' ? '#3b82f6' : '#f3f4f6'};
  color: ${props => props.variant === 'primary' ? 'white' : '#374151'};

  &:hover {
    background: ${props => props.variant === 'primary' ? '#2563eb' : '#e5e7eb'};
    transform: translateY(-1px);
  }
`;

const CurrentView = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;

  .view-title {
    font-size: 1.5rem;
    font-weight: 700;
    color: #1e293b;
  }

  .section-title {
    font-size: 1.125rem;
    font-weight: 600;
    color: #64748b;
  }
`;

const CalendarGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 1.5rem;
  margin-top: 1rem;
`;

const MonthCalendar = styled.div`
  background: white;
  border-radius: 1rem;
  padding: 1.5rem;
  box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.1);
  border: 1px solid #e5e7eb;
`;

const MonthHeader = styled.div`
  text-align: center;
  margin-bottom: 1rem;

  h3 {
    font-size: 1.25rem;
    font-weight: 700;
    color: #1e293b;
    margin: 0;
    text-transform: uppercase;
  }
`;

const DaysHeader = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
  margin-bottom: 0.5rem;
`;

const DayHeader = styled.div`
  text-align: center;
  font-size: 0.75rem;
  font-weight: 600;
  color: #64748b;
  padding: 0.5rem 0;
`;

const DaysGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 2px;
`;

const DayCell = styled.div<{ 
  isCurrentMonth?: boolean; 
  isToday?: boolean; 
  hasEvent?: boolean;
  isWeekend?: boolean;
  isCoordinated?: boolean;
}>`
  aspect-ratio: 1;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 0.875rem;
  font-weight: ${props => props.isToday ? '700' : '500'};
  border: 1px solid #e2e8f0;
  cursor: ${props => props.hasEvent ? 'pointer' : 'default'};
  transition: all 0.2s ease-in-out;
  position: relative;

  background: ${props => {
    if (props.hasEvent && props.isCoordinated) return '#dcfce7'; // Verde para eventos coordinados
    if (props.hasEvent && !props.isCoordinated) return '#fee2e2'; // Rojo para eventos no coordinados
    if (props.isToday) return '#3b82f6';
    if (props.isWeekend) return '#f8fafc';
    return 'white';
  }};

  color: ${props => {
    if (props.isToday) return 'white';
    if (props.hasEvent && props.isCoordinated) return '#166534'; // Verde oscuro para texto
    if (props.hasEvent && !props.isCoordinated) return '#991b1b'; // Rojo oscuro para texto
    if (!props.isCurrentMonth) return '#cbd5e1';
    return '#374151';
  }};

  &:hover {
    background: ${props => {
      if (props.hasEvent && props.isCoordinated) return '#bbf7d0'; // Verde más claro al hover
      if (props.hasEvent && !props.isCoordinated) return '#fecaca'; // Rojo más claro al hover
      return '#f1f5f9';
    }};
    transform: scale(1.05);
    z-index: 1;
  }

  ${props => !props.isCurrentMonth && `
    background: #f8fafc;
    color: #cbd5e1;
  `}
`;

const LegendSection = styled.div`
  background: white;
  border-radius: 0.75rem;
  padding: 1rem;
  margin-bottom: 1rem;
  box-shadow: 0 2px 4px -1px rgba(0, 0, 0, 0.1);
  border: 1px solid #e5e7eb;
`;

const LegendTitle = styled.h3`
  font-size: 0.875rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0 0 0.75rem 0;
`;

const LegendGrid = styled.div`
  display: flex;
  gap: 1.5rem;
`;

const LegendItem = styled.div`
  display: flex;
  align-items: center;
  gap: 0.375rem;
`;

const LegendColor = styled.div<{ coordinated: boolean }>`
  width: 16px;
  height: 16px;
  border-radius: 3px;
  background: ${props => props.coordinated ? '#dcfce7' : '#fee2e2'};
  border: 1px solid ${props => props.coordinated ? '#166534' : '#991b1b'};
`;

const LegendText = styled.span`
  font-size: 0.75rem;
  font-weight: 500;
  color: #374151;
`;

const EventTooltip = styled.div`
  position: fixed;
  background: white;
  border: 1px solid #e2e8f0;
  border-radius: 0.75rem;
  padding: 1rem;
  box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1);
  z-index: 1000;
  min-width: 280px;
  opacity: 0;
  visibility: hidden;
  transition: all 0.2s ease-in-out;
  pointer-events: none;

  &.visible {
    opacity: 1;
    visibility: visible;
  }
`;

const TooltipHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 0.75rem;
  padding-bottom: 0.5rem;
  border-bottom: 1px solid #e2e8f0;
`;

const TooltipTitle = styled.h4`
  font-size: 1rem;
  font-weight: 600;
  color: #1e293b;
  margin: 0;
`;

const TooltipStatus = styled.span<{ coordinated: boolean }>`
  font-size: 0.75rem;
  font-weight: 500;
  padding: 0.25rem 0.5rem;
  border-radius: 0.375rem;
  background: ${props => props.coordinated ? '#dcfce7' : '#fee2e2'};
  color: ${props => props.coordinated ? '#166534' : '#991b1b'};
`;

const TooltipContent = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
`;

const TooltipRow = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
`;

const TooltipLabel = styled.span`
  font-size: 0.875rem;
  font-weight: 500;
  color: #64748b;
  min-width: 80px;
`;

const TooltipValue = styled.span`
  font-size: 0.875rem;
  color: #1e293b;
`;

const WhatsAppButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  background: #25d366;
  color: white;
  border: none;
  border-radius: 0.5rem;
  padding: 0.5rem 0.75rem;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  pointer-events: auto;

  &:hover {
    background: #128c7e;
    transform: translateY(-1px);
  }
`;

const Events: React.FC = () => {
  const navigate = useNavigate();
  const [selectedSalon, setSelectedSalon] = useState('San Telmo');
  const [selectedZone, setSelectedZone] = useState('TODAS');
  const [selectedYear, setSelectedYear] = useState('2025');
  const [tooltipData, setTooltipData] = useState<{
    visible: boolean;
    event: any;
    position: { x: number; y: number };
  } | null>(null);

  // Datos de ejemplo para eventos - Todo el año 2025 con estado de coordinación
  const events = [
    // ENERO 2025
    { 
      date: '2025-01-03', 
      coordinated: false,
      type: 'XV',
      time: '21:00',
      client: 'María González',
      phone: '+54 9 11 1234-5678'
    },
    { 
      date: '2025-01-04', 
      coordinated: true,
      type: 'Casamiento',
      time: '20:00',
      client: 'Carlos Rodríguez',
      phone: '+54 9 11 2345-6789'
    },
    { 
      date: '2025-01-10', 
      coordinated: false,
      type: 'Cumpleaños',
      time: '22:00',
      client: 'Ana López',
      phone: '+54 9 11 3456-7890'
    },
    { 
      date: '2025-01-11', 
      coordinated: true,
      type: 'Religioso',
      time: '19:00',
      client: 'Pedro Martínez',
      phone: '+54 9 11 4567-8901'
    },
    { 
      date: '2025-01-17', 
      coordinated: false,
      type: 'XV',
      time: '21:30',
      client: 'Lucía Fernández',
      phone: '+54 9 11 5678-9012'
    },
    { 
      date: '2025-01-18', 
      coordinated: true,
      type: 'Casamiento',
      time: '20:30',
      client: 'Roberto Silva',
      phone: '+54 9 11 6789-0123'
    },
    { 
      date: '2025-01-24', 
      coordinated: false,
      type: 'Cumpleaños',
      time: '22:30',
      client: 'Carmen Ruiz',
      phone: '+54 9 11 7890-1234'
    },
    { 
      date: '2025-01-25', 
      coordinated: true,
      type: 'Religioso',
      time: '19:30',
      client: 'Diego Morales',
      phone: '+54 9 11 8901-2345'
    },
    { 
      date: '2025-01-31', 
      coordinated: false,
      type: 'XV',
      time: '21:00',
      client: 'Valentina Castro',
      phone: '+54 9 11 9012-3456'
    },

    // FEBRERO 2025
    { 
      date: '2025-02-01', 
