import React, { useState } from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { 
  FaArrowLeft, 
  FaPlay, 
  FaPause, 
  FaDownload, 
  FaFolder,
  FaMusic,
  FaClock,
  FaFileAudio
} from 'react-icons/fa';

interface ShowTrack {
  id: string;
  title: string;
  category: string;
  duration: string;
  fileSize: string;
  driveUrl: string;
  description?: string;
}

const ShowsContainer = styled.div`
  padding: 2rem;
  max-width: 1400px;
  margin: 0 auto;
`;

const HeaderSection = styled.div`
