import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Layout from '../components/Layout';
import { 
  FaCalendarAlt, 
  FaMusic, 
  FaTools, 
  FaUsers, 
  FaExclamationTriangle,
  FaCheckCircle,
  FaClock,
  FaPlay,
  FaBell,
  FaChartLine,
  FaArrowUp,
  FaArrowDown,
  FaPlus,
  FaEye,
  FaEdit,
  FaTrash,
  FaStar,
  FaUserFriends,
  FaHeadphones,
  FaLightbulb
} from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';

const DashboardContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const WelcomeSection = styled.div`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 1rem;
  padding: 2rem;
  color: white;
  margin-bottom: 1rem;
  
  .welcome-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 1rem;
    
    h1 {
      font-size: 1.875rem;
