const Dashboard: React.FC = () => {
  const { user } = useAuth();
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date());
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  // Datos de eventos para calcular estadísticas
  const allEvents = [
    {
      id: '1',
      title: 'Boda María & Juan',
      date: '2024-08-15',
      time: '20:00',
      client: 'María González',
      guests: 150,
      type: 'wedding',
      status: 'confirmed',
      coordinated: true,
      equipment: [],
      notes: 'Boda elegante con música en vivo'
    },
    {
      id: '2',
      title: 'Cumpleaños 15 Años - Ana',
      date: '2024-08-18',
      time: '19:00',
      client: 'Ana Rodríguez',
      guests: 80,
      type: 'birthday',
      status: 'pending',
      coordinated: false,
      equipment: [],
      notes: 'Tema: Princesas Disney'
    },
    {
      id: '3',
      title: 'Evento Corporativo TechCorp',
      date: '2024-08-20',
      time: '18:00',
      client: 'TechCorp S.A.',
      guests: 200,
      type: 'corporate',
      status: 'confirmed',
      coordinated: true,
      equipment: [],
      notes: 'Presentación de nuevos productos'
    },
    {
      id: '4',
      title: 'Graduación Universidad',
      date: '2024-08-22',
      time: '21:00',
      client: 'Universidad Nacional',
      guests: 300,
      type: 'other',
      status: 'in-progress',
      coordinated: false,
      equipment: [],
      notes: 'Ceremonia de graduación'
    },
    {
      id: '5',
      title: 'Boda Carlos & Laura',
      date: '2024-08-25',
      time: '19:30',
      client: 'Carlos Martínez',
      guests: 120,
      type: 'wedding',
      status: 'confirmed',
      coordinated: true,
      equipment: [],
      notes: 'Boda tradicional'
    },
    {
      id: '6',
      title: 'Cumpleaños Empresarial',
      date: '2024-08-28',
      time: '20:00',
      client: 'Empresa ABC',
      guests: 180,
      type: 'corporate',
      status: 'pending',
      coordinated: false,
      equipment: [],
      notes: 'Celebración 10 años'
    }
  ];

  // Calcular estadísticas basadas en coordinación
  const coordinatedEvents = allEvents.filter(event => event.coordinated).length;
  const uncoordinatedEvents = allEvents.filter(event => !event.coordinated).length;

  const stats = [
    {
      title: 'Eventos Coordinados',
      value: coordinatedEvents.toString(),
      icon: <FaCheckCircle />,
      color: '#10b981',
      trend: 'positive',
      trendValue: `${Math.round((coordinatedEvents / allEvents.length) * 100)}%`,
      chartData: [coordinatedEvents]
    },
    {
      title: 'Eventos Sin Coordinar',
      value: uncoordinatedEvents.toString(),
      icon: <FaExclamationTriangle />,
      color: '#ef4444',
      trend: 'negative',
      trendValue: `${Math.round((uncoordinatedEvents / allEvents.length) * 100)}%`,
      chartData: [uncoordinatedEvents]
    }
  ];

  // Obtener los próximos 4 eventos ordenados por fecha
  const upcomingEvents = allEvents
    .sort((a, b) => new Date(a.date).getTime() - new Date(b.date).getTime())
    .slice(0, 4)
    .map(event => ({
      id: event.id,
      title: event.title,
      date: new Date(event.date).toLocaleDateString('es-ES', { 
        day: 'numeric', 
        month: 'short' 
      }) + ', ' + event.time,
      client: event.client,
      status: event.coordinated ? 'confirmed' : 'pending',
      guests: event.guests,
      type: event.type,
      coordinated: event.coordinated
    }));

  const alerts = [
    {
      id: '1',
      title: 'Eventos Sin Coordinar',
      description: `${uncoordinatedEvents} eventos requieren coordinación inmediata`,
      type: 'warning',
      time: 'Ahora'
    },
    {
      id: '2',
      title: 'Coordinación Completada',
      description: 'Boda María & Juan y Evento TechCorp ya están coordinados',
      type: 'info',
      time: '1 hora'
    },
    {
      id: '3',
      title: 'Recordatorio de Coordinación',
      description: 'Cumpleaños 15 Años necesita coordinación antes del 18 de agosto',
      type: 'warning',
      time: '2 horas'
    }
  ];

  const recentActivity = [
    {
      id: '1',
      text: `Evento coordinado: "Boda Carlos & Laura" (${coordinatedEvents}/${allEvents.length})`,
      time: 'Hace 15 minutos',
      icon: <FaCheckCircle />
    },
    {
      id: '2',
      text: 'Nuevo evento sin coordinar: "Cumpleaños Empresarial"',
      time: 'Hace 1 hora',
      icon: <FaExclamationTriangle />
    },
    {
      id: '3',
      text: 'Coordinación completada: "Evento Corporativo TechCorp"',
      time: 'Hace 2 horas',
      icon: <FaCheckCircle />
    },
    {
      id: '4',
      text: `Tasa de coordinación actual: ${Math.round((coordinatedEvents / allEvents.length) * 100)}%`,
      time: 'Hace 3 horas',
      icon: <FaChartLine />
    }
  ];

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'confirmed': return <FaCheckCircle />;
      case 'pending': return <FaClock />;
      case 'in-progress': return <FaPlay />;
      default: return <FaCalendarAlt />;
    }
  };

  const getGreeting = () => {
    const hour = currentTime.getHours();
    if (hour < 12) return 'Buenos días';
    if (hour < 18) return 'Buenas tardes';
    return 'Buenas noches';
  };

  const renderMiniCalendar = () => {
    const today = new Date();
    const daysInMonth = new Date(today.getFullYear(), today.getMonth() + 1, 0).getDate();
    const firstDay = new Date(today.getFullYear(), today.getMonth(), 1).getDay();
    
    const days = [];
    const dayNames = ['D', 'L', 'M', 'M', 'J', 'V', 'S'];
    
    // Headers
    dayNames.forEach(day => {
      days.push(<div key={`header-${day}`} className="day-header">{day}</div>);
    });
    
    // Empty days
    for (let i = 0; i < firstDay; i++) {
      days.push(<div key={`empty-${i}`} className="day"></div>);
    }
    
    // Days of month
    for (let day = 1; day <= daysInMonth; day++) {
      const isToday = day === today.getDate();
      const currentDate = new Date(today.getFullYear(), today.getMonth(), day);
      const dateString = currentDate.toISOString().split('T')[0];
      
      // Buscar eventos para esta fecha
      const eventsOnDay = allEvents.filter(event => event.date === dateString);
      const hasEvent = eventsOnDay.length > 0;
      const hasCoordinatedEvent = eventsOnDay.some(event => event.coordinated);
      const hasUncoordinatedEvent = eventsOnDay.some(event => !event.coordinated);
      
      let eventClass = '';
      if (hasCoordinatedEvent && hasUncoordinatedEvent) {
        eventClass = 'has-event-mixed';
      } else if (hasCoordinatedEvent) {
        eventClass = 'has-event-coordinated';
      } else if (hasUncoordinatedEvent) {
        eventClass = 'has-event-uncoordinated';
      }
      
      days.push(
        <div 
          key={day} 
          className={`day ${isToday ? 'today' : ''} ${eventClass}`}
          title={hasEvent ? `${eventsOnDay.length} evento(s) en esta fecha` : ''}
        >
          {day}
        </div>
      );
    }
    
    return days;
  };

  return (
    <Layout 
      title="Dashboard" 
      description={`${getGreeting()}, ${user?.name}. Aquí tienes un resumen completo de las actividades.`}
    >
      <DashboardContainer>
        <WelcomeSection>
          <div className="welcome-header">
            <div>
              <h1>{getGreeting()}, {user?.name}!</h1>
              <p>Aquí tienes un resumen completo de las actividades del día</p>
            </div>
            <div style={{ textAlign: 'right' }}>
              <div style={{ fontSize: '0.875rem', opacity: 0.8 }}>
                {currentTime.toLocaleDateString('es-ES', { 
                  weekday: 'long', 
                  year: 'numeric', 
                  month: 'long', 
                  day: 'numeric' 
                })}
              </div>
              <div style={{ fontSize: '1.25rem', fontWeight: '600' }}>
                {currentTime.toLocaleTimeString('es-ES', { 
                  hour: '2-digit', 
                  minute: '2-digit' 
                })}
              </div>
            </div>
          </div>
          
          <div className="welcome-stats">
            <div className="stat-item">
              <div className="stat-number">{allEvents.length}</div>
              <div className="stat-label">Total Eventos</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">{coordinatedEvents}</div>
              <div className="stat-label">Coordinados</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">{uncoordinatedEvents}</div>
              <div className="stat-label">Sin Coordinar</div>
            </div>
            <div className="stat-item">
              <div className="stat-number">{Math.round((coordinatedEvents / allEvents.length) * 100)}%</div>
              <div className="stat-label">Tasa de Coordinación</div>
            </div>
          </div>
        </WelcomeSection>

        <StatsGrid>
          {stats.map((stat, index) => (
            <StatCard key={index} color={stat.color} trend={stat.trend}>
              <div className="stat-header">
                <div className="stat-icon">
                  {stat.icon}
                </div>
                <div className="stat-trend">
                  {stat.trend === 'positive' ? <FaArrowUp /> : <FaArrowDown />}
                  {stat.trendValue}
                </div>
              </div>
              <div className="stat-value">{stat.value}</div>
              <div className="stat-label">{stat.title}</div>
              <div className="stat-chart">
                <div 
                  className="chart-bar" 
                  style={{ 
                    width: `${Math.max(...stat.chartData)}%`,
                    background: stat.color 
                  }}
                />
              </div>
            </StatCard>
          ))}
        </StatsGrid>

        <ContentGrid>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
            <Section>
              <div className="section-header">
                <h3>
                  <FaCalendarAlt />
                  Próximos Eventos
                </h3>
                <span className="view-all">Ver todos</span>
              </div>
              <EventList>
                {upcomingEvents.map((event) => (
                  <EventItem key={event.id} status={event.status}>
                    <div className="event-icon">
                      {getStatusIcon(event.status)}
                    </div>
                    <div className="event-details">
                      <div className="event-title">{event.title}</div>
                      <div className="event-info">
                        <span>{event.date}</span>
                        <span>•</span>
                        <span>{event.client}</span>
                        <span>•</span>
                        <span>{event.guests} invitados</span>
                      </div>
                    </div>
                    <div className="event-status">{event.status}</div>
                  </EventItem>
                ))}
              </EventList>
            </Section>

            <Section>
              <div className="section-header">
                <h3>
                  <FaBell />
                  Alertas y Notificaciones
                </h3>
              </div>
              <AlertList>
                {alerts.map((alert) => (
                  <AlertItem key={alert.id} type={alert.type}>
                    <div className="alert-icon">
                      {alert.type === 'warning' ? <FaExclamationTriangle /> : <FaCheckCircle />}
                    </div>
                    <div className="alert-content">
                      <div className="alert-title">{alert.title}</div>
                      <div className="alert-description">
                        {alert.description}
                        <span style={{ marginLeft: '0.5rem', fontWeight: '500' }}>
                          • Hace {alert.time}
                        </span>
                      </div>
                    </div>
                  </AlertItem>
                ))}
              </AlertList>
            </Section>
          </div>

          <div style={{ display: 'flex', flexDirection: 'column', gap: '1.5rem' }}>
            <Section>
              <div className="section-header">
                <h3>
                  <FaCalendarAlt />
                  Calendario
                </h3>
              </div>
              <MiniCalendar>
                <div className="calendar-header">
                  <h4>{currentTime.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' })}</h4>
                </div>
                <div className="calendar-grid">
                  {renderMiniCalendar()}
                </div>
              </MiniCalendar>
            </Section>

            <Section>
              <div className="section-header">
                <h3>
                  <FaUserFriends />
                  Actividad Reciente
                </h3>
              </div>
              <ActivityFeed>
                {recentActivity.map((activity) => (
                  <div key={activity.id} className="activity-item">
                    <div className="activity-icon">
                      {activity.icon}
                    </div>
                    <div className="activity-content">
                      <div className="activity-text">{activity.text}</div>
                      <div className="activity-time">{activity.time}</div>
                    </div>
                  </div>
                ))}
              </ActivityFeed>
            </Section>

            <Section>
              <div className="section-header">
                <h3>
                  <FaLightbulb />
                  Acciones Rápidas
                </h3>
              </div>
              <QuickActions>
                <QuickActionButton>
                  <div className="action-icon">
                    <FaMusic />
                  </div>
                  <div className="action-text">Crear Playlist</div>
                </QuickActionButton>
                <QuickActionButton>
                  <div className="action-icon">
                    <FaCalendarAlt />
                  </div>
                  <div className="action-text">Nuevo Evento</div>
                </QuickActionButton>
                <QuickActionButton>
                  <div className="action-icon">
                    <FaTools />
                  </div>
                  <div className="action-text">Reportar Problema</div>
                </QuickActionButton>
                <QuickActionButton>
                  <div className="action-icon">
                    <FaUsers />
                  </div>
                  <div className="action-text">Ver Equipo</div>
                </QuickActionButton>
              </QuickActions>
            </Section>
          </div>
        </ContentGrid>
      </DashboardContainer>
    </Layout>
  );
};

export default Dashboard;
