import React, { useState } from 'react';
import styled from 'styled-components';
import Layout from '../components/Layout';
import { 
  FaCalendarAlt, 
  FaPlus, 
  FaSearch, 
  FaFilter,
  FaEye,
  FaEdit,
  FaTrash,
  FaCheckCircle,
  FaClock,
  FaExclamationTriangle,
  FaUsers,
  FaMapMarkerAlt,
  FaMusic,
  FaChevronLeft,
  FaChevronRight
} from 'react-icons/fa';
import { Event } from '../types';

const EventsContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const HeaderSection = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
`;

const SearchAndFilters = styled.div`
  display: flex;
  gap: 1rem;
  align-items: center;
  flex-wrap: wrap;
`;

const SearchBox = styled.div`
  position: relative;

  input {
    padding: 0.75rem 1rem 0.75rem 2.5rem;
    border: 1px solid #e2e8f0;
    border-radius: 0.5rem;
    font-size: 0.875rem;
    width: 300px;

    &:focus {
      outline: none;
      border-color: #3b82f6;
      box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
    }
  }

  .search-icon {
    position: absolute;
    left: 0.75rem;
    top: 50%;
    transform: translateY(-50%);
    color: #9ca3af;
  }
`;

const FilterButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1rem;
  background: white;
  border: 1px solid #e2e8f0;
  border-radius: 0.5rem;
  color: #374151;
  font-size: 0.875rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    border-color: #3b82f6;
    color: #3b82f6;
  }
`;

const AddEventButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  background: #3b82f6;
  color: white;
  border: none;
  border-radius: 0.5rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    background: #2563eb;
    transform: translateY(-1px);
  }
`;

const StatsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1rem;
  margin-bottom: 1.5rem;
`;

interface StatCardProps {
  color?: string;
}

const StatCard = styled.div<StatCardProps>`
  background: white;
  border-radius: 0.75rem;
  padding: 1.25rem;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
  border-left: 4px solid ${props => props.color || '#3b82f6'};

  .stat-header {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    margin-bottom: 0.75rem;

    .stat-icon {
      width: 40px;
      height: 40px;
      border-radius: 0.5rem;
      display: flex;
      align-items: center;
      justify-content: center;
      background: ${props => props.color || '#3b82f6'};
      color: white;
    }
  }

  .stat-value {
    font-size: 1.5rem;
    font-weight: 700;
    color: #1e293b;
    margin-bottom: 0.25rem;
  }

  .stat-label {
    color: #64748b;
    font-size: 0.875rem;
  }
`;

const EventsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
  gap: 1.5rem;
`;

const EventCard = styled.div`
  background: white;
  border-radius: 0.75rem;
  padding: 1.5rem;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
  border: 1px solid #e2e8f0;
  transition: all 0.2s ease-in-out;

  &:hover {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
    transform: translateY(-2px);
  }
`;

const EventHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 1rem;
`;

interface EventTypeProps {
  type: string;
}

const EventType = styled.span<EventTypeProps>`
  padding: 0.25rem 0.75rem;
  border-radius: 1rem;
  font-size: 0.75rem;
  font-weight: 500;
  text-transform: uppercase;
  background: ${props => {
    switch (props.type) {
      case 'wedding': return '#fef3c7';
      case 'birthday': return '#dbeafe';
      case 'corporate': return '#d1fae5';
      default: return '#f3f4f6';
    }
  }};
  color: ${props => {
    switch (props.type) {
      case 'wedding': return '#92400e';
      case 'birthday': return '#1e40af';
      case 'corporate': return '#065f46';
      default: return '#374151';
    }
  }};
`;

interface EventStatusProps {
  status: string;
}

const EventStatus = styled.span<EventStatusProps>`
  padding: 0.25rem 0.75rem;
  border-radius: 1rem;
  font-size: 0.75rem;
  font-weight: 500;
  text-transform: uppercase;
  background: ${props => {
    switch (props.status) {
      case 'confirmed': return '#d1fae5';
      case 'pending': return '#fef3c7';
      case 'in-progress': return '#dbeafe';
      case 'completed': return '#f3f4f6';
      default: return '#fee2e2';
    }
  }};
  color: ${props => {
    switch (props.status) {
      case 'confirmed': return '#065f46';
      case 'pending': return '#92400e';
      case 'in-progress': return '#1e40af';
      case 'completed': return '#374151';
      default: return '#991b1b';
    }
  }};
`;

const EventTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #1e293b;
  margin-bottom: 1rem;
`;

const EventDetails = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
  margin-bottom: 1.5rem;
`;

const EventDetail = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: #64748b;
  font-size: 0.875rem;

  .detail-icon {
    color: #9ca3af;
    width: 16px;
  }
`;

const EventActions = styled.div`
  display: flex;
  gap: 0.5rem;
`;

const ActionButton = styled.button`
  display: flex;
  align-items: center;
  gap: 0.25rem;
  padding: 0.5rem 0.75rem;
  border: 1px solid #e2e8f0;
  border-radius: 0.375rem;
  background: white;
  color: #64748b;
  font-size: 0.75rem;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    border-color: #3b82f6;
    color: #3b82f6;
  }

  &.primary {
    background: #3b82f6;
    color: white;
    border-color: #3b82f6;

    &:hover {
      background: #2563eb;
    }
  }

  &.danger {
    &:hover {
      border-color: #ef4444;
      color: #ef4444;
    }
  }
`;

const EmptyState = styled.div`
  text-align: center;
  padding: 3rem;
  color: #64748b;

  .empty-icon {
    font-size: 3rem;
    color: #cbd5e1;
    margin-bottom: 1rem;
  }

  h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: #374151;
  }

  p {
    margin-bottom: 1.5rem;
  }
`;

const ViewToggle = styled.div`
  display: flex;
  gap: 0.5rem;
  margin-bottom: 1.5rem;
`;

const ViewButton = styled.button<{ active: boolean }>`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.75rem 1rem;
  border: 1px solid #e2e8f0;
  border-radius: 0.5rem;
  background: ${props => props.active ? '#3b82f6' : 'white'};
  color: ${props => props.active ? 'white' : '#374151'};
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    border-color: #3b82f6;
    color: ${props => props.active ? 'white' : '#3b82f6'};
  }
`;

const CalendarContainer = styled.div`
  background: white;
  border-radius: 0.75rem;
  padding: 1.5rem;
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
`;

const CalendarHeader = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 1.5rem;
`;

const CalendarTitle = styled.h2`
  font-size: 1.25rem;
  font-weight: 600;
  color: #1e293b;
`;

const CalendarNavigation = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const CalendarNavButton = styled.button`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border: 1px solid #e2e8f0;
  border-radius: 0.5rem;
  background: white;
  color: #374151;
  cursor: pointer;
  transition: all 0.2s ease-in-out;

  &:hover {
    border-color: #3b82f6;
    color: #3b82f6;
  }
`;

const CalendarGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: 1px;
  background: #e2e8f0;
  border-radius: 0.5rem;
  overflow: hidden;
`;

const CalendarDayHeader = styled.div`
  background: #f8fafc;
  padding: 0.75rem;
  text-align: center;
  font-weight: 600;
  font-size: 0.875rem;
  color: #64748b;
`;

const CalendarDay = styled.div<{ isToday?: boolean; hasEvent?: boolean; isOtherMonth?: boolean }>`
  background: white;
  padding: 0.75rem;
  min-height: 80px;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  position: relative;

  ${props => props.isToday && `
    background: #dbeafe;
    border: 2px solid #3b82f6;
  `}

  ${props => props.hasEvent && `
    background: #f0f9ff;
  `}

  ${props => props.isOtherMonth && `
    color: #9ca3af;
  `}

  &:hover {
    background: #f8fafc;
  }
`;

const DayNumber = styled.div`
  font-weight: 600;
  margin-bottom: 0.25rem;
`;

const EventIndicator = styled.div`
  width: 6px;
  height: 6px;
  background: #3b82f6;
  border-radius: 50%;
  margin-top: 0.25rem;
`;

const Events: React.FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('all');
  const [viewMode, setViewMode] = useState<'cards' | 'calendar'>('cards');
  const [currentMonth, setCurrentMonth] = useState(new Date());

  // Datos de ejemplo para eventos
  const events: Event[] = [
    {
      id: '1',
      title: 'Boda María & Juan',
      date: '2024-08-15',
      time: '20:00',
      client: 'María González',
      guests: 150,
      type: 'wedding',
      status: 'confirmed',
      equipment: [],
      notes: 'Boda elegante con música en vivo'
    },
    {
      id: '2',
      title: 'Cumpleaños 15 Años - Ana',
      date: '2024-08-18',
      time: '19:00',
      client: 'Ana Rodríguez',
      guests: 80,
      type: 'birthday',
      status: 'pending',
      equipment: [],
      notes: 'Tema: Princesas Disney'
    },
    {
      id: '3',
      title: 'Evento Corporativo TechCorp',
      date: '2024-08-20',
      time: '18:00',
      client: 'TechCorp S.A.',
      guests: 200,
      type: 'corporate',
      status: 'confirmed',
      equipment: [],
      notes: 'Presentación de nuevos productos'
    },
    {
      id: '4',
      title: 'Graduación Universidad',
      date: '2024-08-22',
      time: '21:00',
      client: 'Universidad Nacional',
      guests: 300,
      type: 'other',
      status: 'in-progress',
      equipment: [],
      notes: 'Ceremonia de graduación'
    }
  ];

  const stats = [
    {
      title: 'Total Eventos',
      value: events.length.toString(),
      icon: <FaCalendarAlt />,
      color: '#3b82f6'
    },
    {
      title: 'Confirmados',
      value: events.filter(e => e.status === 'confirmed').length.toString(),
      icon: <FaCheckCircle />,
      color: '#10b981'
    },
    {
      title: 'Pendientes',
      value: events.filter(e => e.status === 'pending').length.toString(),
      icon: <FaClock />,
      color: '#f59e0b'
    },
    {
      title: 'En Progreso',
      value: events.filter(e => e.status === 'in-progress').length.toString(),
      icon: <FaExclamationTriangle />,
      color: '#ef4444'
    }
  ];

  const getTypeLabel = (type: string) => {
    const labels = {
      wedding: 'Boda',
      birthday: 'Cumpleaños',
      corporate: 'Corporativo',
      other: 'Otro'
    };
    return labels[type as keyof typeof labels] || type;
  };

  const getStatusLabel = (status: string) => {
    const labels = {
      confirmed: 'Confirmado',
      pending: 'Pendiente',
      'in-progress': 'En Progreso',
      completed: 'Completado',
      cancelled: 'Cancelado'
    };
    return labels[status as keyof typeof labels] || status;
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('es-ES', {
      day: 'numeric',
      month: 'long',
      year: 'numeric'
    });
  };

  const filteredEvents = events.filter(event => {
    const matchesSearch = event.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         event.client.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = selectedFilter === 'all' || event.status === selectedFilter;
    return matchesSearch && matchesFilter;
  });

  // Funciones del calendario
  const getDaysInMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
  };

  const getFirstDayOfMonth = (date: Date) => {
    return new Date(date.getFullYear(), date.getMonth(), 1).getDay();
  };

  const isToday = (date: Date) => {
    const today = new Date();
    return date.getDate() === today.getDate() &&
           date.getMonth() === today.getMonth() &&
           date.getFullYear() === today.getFullYear();
  };

  const hasEventOnDate = (date: Date) => {
    const dateString = date.toISOString().split('T')[0];
    return events.some(event => event.date === dateString);
  };

  const getMonthName = (date: Date) => {
    return date.toLocaleDateString('es-ES', { month: 'long', year: 'numeric' });
  };

  const renderCalendar = () => {
    const daysInMonth = getDaysInMonth(currentMonth);
    const firstDay = getFirstDayOfMonth(currentMonth);
    const days = [];

    // Días del mes anterior
    const prevMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1);
    const daysInPrevMonth = getDaysInMonth(prevMonth);
    for (let i = firstDay - 1; i >= 0; i--) {
      const day = new Date(prevMonth.getFullYear(), prevMonth.getMonth(), daysInPrevMonth - i);
      days.push(
        <CalendarDay key={`prev-${i}`} isOtherMonth>
          <DayNumber>{day.getDate()}</DayNumber>
        </CalendarDay>
      );
    }

    // Días del mes actual
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(currentMonth.getFullYear(), currentMonth.getMonth(), day);
      days.push(
        <CalendarDay 
          key={day} 
          isToday={isToday(date)}
          hasEvent={hasEventOnDate(date)}
        >
          <DayNumber>{day}</DayNumber>
          {hasEventOnDate(date) && <EventIndicator />}
        </CalendarDay>
      );
    }

    // Días del mes siguiente
    const remainingDays = 42 - days.length; // 6 semanas * 7 días
    for (let day = 1; day <= remainingDays; day++) {
      const nextMonth = new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, day);
      days.push(
        <CalendarDay key={`next-${day}`} isOtherMonth>
          <DayNumber>{day}</DayNumber>
        </CalendarDay>
      );
    }

    return days;
  };

  const goToPreviousMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1, 1));
  };

  const goToNextMonth = () => {
    setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1, 1));
  };

  return (
    <Layout 
      title="Eventos" 
      description="Gestiona y visualiza todos los eventos programados"
    >
      <EventsContainer>
        <HeaderSection>
          <SearchAndFilters>
            <SearchBox>
              <FaSearch className="search-icon" />
              <input
                type="text"
                placeholder="Buscar eventos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </SearchBox>
            <FilterButton>
              <FaFilter />
              Filtros
            </FilterButton>
          </SearchAndFilters>
          <AddEventButton>
            <FaPlus />
            Nuevo Evento
          </AddEventButton>
        </HeaderSection>

        <StatsGrid>
          {stats.map((stat, index) => (
            <StatCard key={index} color={stat.color}>
              <div className="stat-header">
                <div className="stat-icon">
                  {stat.icon}
                </div>
              </div>
              <div className="stat-value">{stat.value}</div>
              <div className="stat-label">{stat.title}</div>
            </StatCard>
          ))}
        </StatsGrid>

        <ViewToggle>
          <ViewButton 
            active={viewMode === 'cards'} 
            onClick={() => setViewMode('cards')}
          >
            <FaCalendarAlt />
            Vista de Tarjetas
          </ViewButton>
          <ViewButton 
            active={viewMode === 'calendar'} 
            onClick={() => setViewMode('calendar')}
          >
            <FaCalendarAlt />
            Vista de Calendario
          </ViewButton>
        </ViewToggle>

        {viewMode === 'calendar' ? (
          <CalendarContainer>
            <CalendarHeader>
              <CalendarTitle>{getMonthName(currentMonth)}</CalendarTitle>
              <CalendarNavigation>
                <CalendarNavButton onClick={goToPreviousMonth}>
                  <FaChevronLeft />
                </CalendarNavButton>
                <CalendarNavButton onClick={goToNextMonth}>
                  <FaChevronRight />
                </CalendarNavButton>
              </CalendarNavigation>
            </CalendarHeader>

            <CalendarGrid>
              <CalendarDayHeader>Dom</CalendarDayHeader>
              <CalendarDayHeader>Lun</CalendarDayHeader>
              <CalendarDayHeader>Mar</CalendarDayHeader>
              <CalendarDayHeader>Mié</CalendarDayHeader>
              <CalendarDayHeader>Jue</CalendarDayHeader>
              <CalendarDayHeader>Vie</CalendarDayHeader>
              <CalendarDayHeader>Sáb</CalendarDayHeader>
              {renderCalendar()}
            </CalendarGrid>
          </CalendarContainer>
        ) : (
          filteredEvents.length === 0 ? (
            <EmptyState>
              <div className="empty-icon">
                <FaCalendarAlt />
              </div>
              <h3>No se encontraron eventos</h3>
              <p>No hay eventos que coincidan con tu búsqueda o filtros.</p>
              <AddEventButton>
                <FaPlus />
                Crear Primer Evento
              </AddEventButton>
            </EmptyState>
          ) : (
            <EventsGrid>
              {filteredEvents.map((event) => (
                <EventCard key={event.id}>
                  <EventHeader>
                    <EventType type={event.type}>
                      {getTypeLabel(event.type)}
                    </EventType>
                    <EventStatus status={event.status}>
                      {getStatusLabel(event.status)}
                    </EventStatus>
                  </EventHeader>

                  <EventTitle>{event.title}</EventTitle>

                  <EventDetails>
                    <EventDetail>
                      <FaCalendarAlt className="detail-icon" />
                      <span>{formatDate(event.date)} - {event.time}</span>
                    </EventDetail>
                    <EventDetail>
                      <FaUsers className="detail-icon" />
                      <span>{event.client}</span>
                    </EventDetail>
                    <EventDetail>
                      <FaMapMarkerAlt className="detail-icon" />
                      <span>{event.guests} invitados</span>
                    </EventDetail>
                    {event.notes && (
                      <EventDetail>
                        <FaMusic className="detail-icon" />
                        <span>{event.notes}</span>
                      </EventDetail>
                    )}
                  </EventDetails>

                  <EventActions>
                    <ActionButton>
                      <FaEye />
                      Ver
                    </ActionButton>
                    <ActionButton className="primary">
                      <FaEdit />
                      Editar
                    </ActionButton>
                    <ActionButton className="danger">
                      <FaTrash />
                      Eliminar
                    </ActionButton>
                  </EventActions>
                </EventCard>
              ))}
            </EventsGrid>
          )
        )}
      </EventsContainer>
    </Layout>
  );
};

export default Events;
